SELECT
    concat('uuid-', toString(rand() % 100000000)) AS uuid,
    ['pageview', 'click', 'signup', 'purchase', 'login', 'logout', 'search', 'view_item', 'add_to_cart', 'checkout'][rand() % 10] AS event,
    concat('{"user_properties":{"email":"user', toString(rand() % 10000), '@example.com","plan":"', ['free', 'pro', 'enterprise'][rand() % 3], '"},"$browser":"', ['Chrome', 'Safari', 'Firefox', 'Edge'][rand() % 4], '","$device":"', ['desktop', 'mobile', 'tablet'][rand() % 3], '","$os":"', ['Windows', 'macOS', 'iOS', 'Android', 'Linux'][rand() % 5], '"}') AS properties,
    toDateTime64(now() - (rand() % 2592000), 6) AS timestamp,
    concat('user_', toString(rand() % 5000)) AS distinct_id,
    concat('loc_', toString(rand() % 100)) AS location_id,
    if((rand() % 5) = 0, concat('funnel_', toString(rand() % 20)), NULL) AS funnel_id,
    if(funnel_id IS NOT NULL, concat('step_', toString((rand() % 5) + 1)), NULL) AS step_id,
    if(funnel_id IS NOT NULL, ['Signup Flow', 'Checkout Process', 'Onboarding', 'Feature Adoption', 'Conversion'][rand() % 5], NULL) AS funnel_name,
    if((rand() % 3) = 0, concat('page_', toString(rand() % 50)), NULL) AS page_id,
    if((rand() % 10) = 0, concat('aff_', toString(rand() % 100)), NULL) AS affiliate_id,
    concat('sess_', toString(rand() % 10000)) AS session_id,
    if((rand() % 4) = 0, ['https://google.com', 'https://facebook.com', 'https://twitter.com', 'https://instagram.com', 'https://linkedin.com'][rand() % 5], NULL) AS session_entry_referrer,
    if(session_entry_referrer IS NOT NULL, ['google.com', 'facebook.com', 'twitter.com', 'instagram.com', 'linkedin.com'][rand() % 5], NULL) AS session_entry_referring_domain,
    if((rand() % 2) = 0, concat('https://app.example.com/', ['home', 'pricing', 'features', 'blog', 'contact'][rand() % 5]), NULL) AS session_entry_url,
    if(session_entry_url IS NOT NULL, 'app.example.com', NULL) AS session_entry_host,
    if(session_entry_url IS NOT NULL, concat('/', ['home', 'pricing', 'features', 'blog', 'contact'][rand() % 5]), NULL) AS session_entry_pathname,
    if((rand() % 3) = 0, ['google', 'facebook', 'twitter', 'linkedin', 'instagram', 'email', 'direct', 'referral'][rand() % 8], NULL) AS utm_source,
    if(utm_source IS NOT NULL, ['cpc', 'social', 'email', 'organic', 'referral', 'display'][rand() % 6], NULL) AS utm_medium,
    if(utm_medium IS NOT NULL, ['summer_sale', 'product_launch', 'retargeting', 'newsletter', 'brand_awareness'][rand() % 5], NULL) AS utm_campaign,
    if(utm_campaign IS NOT NULL, ['image', 'banner', 'text', 'video'][rand() % 4], NULL) AS utm_content,
    if(utm_campaign IS NOT NULL, ['software', 'analytics', 'dashboard', 'tracking'][rand() % 4], NULL) AS utm_term,
    if((rand() % 10) = 0, 'google', NULL) AS gad_source,
    if((rand() % 10) = 0, concat('mc_', toString(rand() % 1000000)), NULL) AS mc_cid,
    if((rand() % 10) = 0, concat('gclid-', toString(rand() % 1000000)), NULL) AS gclid,
    if(gclid IS NOT NULL, ['g', 'aw_ds'][rand() % 2], NULL) AS gclsrc,
    if((rand() % 20) = 0, concat('dclid-', toString(rand() % 1000000)), NULL) AS dclid,
    if((rand() % 10) = 0, concat('GB', toString(rand() % 1000000)), NULL) AS gbraid,
    if((rand() % 10) = 0, concat('WB', toString(rand() % 1000000)), NULL) AS wbraid,
    if((rand() % 10) = 0, concat('fb_', toString(rand() % 1000000)), NULL) AS fbclid,
    if((rand() % 15) = 0, concat('ms_', toString(rand() % 1000000)), NULL) AS msclkid,
    if((rand() % 15) = 0, concat('tw_', toString(rand() % 1000000)), NULL) AS twclid,
    if((rand() % 15) = 0, concat('li_', toString(rand() % 1000000)), NULL) AS li_fat_id,
    if((rand() % 15) = 0, concat('ig_', toString(rand() % 1000000)), NULL) AS igshid,
    if((rand() % 15) = 0, concat('tt_', toString(rand() % 1000000)), NULL) AS ttclid,
    if((rand() % 15) = 0, concat('rd_', toString(rand() % 1000000)), NULL) AS rdt_cid,
    if((rand() % 15) = 0, concat('epik_', toString(rand() % 1000000)), NULL) AS epik,
    if((rand() % 20) = 0, concat('qc_', toString(rand() % 1000000)), NULL) AS qclid,
    if((rand() % 20) = 0, concat('sc_', toString(rand() % 1000000)), NULL) AS sccid,
    if((rand() % 20) = 0, concat('ir_', toString(rand() % 1000000)), NULL) AS irclid,
    if((rand() % 20) = 0, concat('kx_', toString(rand() % 1000000)), NULL) AS _kx,
    if((rand() % 4) = 0, ['https://google.com', 'https://facebook.com', 'https://twitter.com', 'https://instagram.com', 'https://linkedin.com'][rand() % 5], NULL) AS referrer,
    if(referrer IS NOT NULL, ['google.com', 'facebook.com', 'twitter.com', 'instagram.com', 'linkedin.com'][rand() % 5], NULL) AS referring_domain,
    concat('pv_', toString(rand() % 1000000)) AS pageview_id,
    rand() % 2 AS is_identified,
    if((rand() % 3) = 0, ['google', 'facebook', 'twitter', 'linkedin', 'instagram', 'email', 'direct', 'referral'][rand() % 8], NULL) AS session_entry_utm_source,
    if(session_entry_utm_source IS NOT NULL, ['cpc', 'social', 'email', 'organic', 'referral', 'display'][rand() % 6], NULL) AS session_entry_utm_medium,
    if(session_entry_utm_medium IS NOT NULL, ['summer_sale', 'product_launch', 'retargeting', 'newsletter', 'brand_awareness'][rand() % 5], NULL) AS session_entry_utm_campaign,
    if(session_entry_utm_campaign IS NOT NULL, ['image', 'banner', 'text', 'video'][rand() % 4], NULL) AS session_entry_utm_content,
    if(session_entry_utm_campaign IS NOT NULL, ['software', 'analytics', 'dashboard', 'tracking'][rand() % 4], NULL) AS session_entry_utm_term,
    if((rand() % 10) = 0, 'google', NULL) AS session_entry_gad_source,
    if((rand() % 10) = 0, concat('mc_', toString(rand() % 1000000)), NULL) AS session_entry_mc_cid,
    if((rand() % 10) = 0, concat('gclid-', toString(rand() % 1000000)), NULL) AS session_entry_gclid,
    if(session_entry_gclid IS NOT NULL, ['g', 'aw_ds'][rand() % 2], NULL) AS session_entry_gclsrc,
    if((rand() % 20) = 0, concat('dclid-', toString(rand() % 1000000)), NULL) AS session_entry_dclid,
    if((rand() % 10) = 0, concat('GB', toString(rand() % 1000000)), NULL) AS session_entry_gbraid,
    if((rand() % 10) = 0, concat('WB', toString(rand() % 1000000)), NULL) AS session_entry_wbraid,
    if((rand() % 10) = 0, concat('fb_', toString(rand() % 1000000)), NULL) AS session_entry_fbclid,
    if((rand() % 15) = 0, concat('ms_', toString(rand() % 1000000)), NULL) AS session_entry_msclkid,
    if((rand() % 15) = 0, concat('tw_', toString(rand() % 1000000)), NULL) AS session_entry_twclid,
    if((rand() % 15) = 0, concat('li_', toString(rand() % 1000000)), NULL) AS session_entry_li_fat_id,
    if((rand() % 15) = 0, concat('ig_', toString(rand() % 1000000)), NULL) AS session_entry_igshid,
    if((rand() % 15) = 0, concat('tt_', toString(rand() % 1000000)), NULL) AS session_entry_ttclid,
    if((rand() % 15) = 0, concat('rd_', toString(rand() % 1000000)), NULL) AS session_entry_rdt_cid,
    if((rand() % 15) = 0, concat('epik_', toString(rand() % 1000000)), NULL) AS session_entry_epik,
    if((rand() % 20) = 0, concat('qc_', toString(rand() % 1000000)), NULL) AS session_entry_qclid,
    if((rand() % 20) = 0, concat('sc_', toString(rand() % 1000000)), NULL) AS session_entry_sccid,
    if((rand() % 20) = 0, concat('ir_', toString(rand() % 1000000)), NULL) AS session_entry_irclid,
    if((rand() % 20) = 0, concat('kx_', toString(rand() % 1000000)), NULL) AS session_entry__kx,
    ['Chrome', 'Safari', 'Firefox', 'Edge', 'Opera'][rand() % 5] AS browser,
    ['desktop', 'mobile', 'tablet'][rand() % 3] AS device_type,
    ['America/New_York', 'Europe/London', 'Asia/Tokyo', 'Australia/Sydney', 'Pacific/Auckland'][rand() % 5] AS timezone,
    concat('https://app.example.com/', ['home', 'dashboard', 'settings', 'profile', 'analytics', 'reports'][rand() % 6]) AS current_url,
    'app.example.com' AS host,
    concat('/', ['home', 'dashboard', 'settings', 'profile', 'analytics', 'reports'][rand() % 6]) AS pathname
FROM numbers(10000)